mapper.insert:
/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE * 작성자 : 홍섭 * 설명 : 사용자 템플릿 저장 * */
SELECT IFNULL(MAX(TEMPLATE_NO), 0) + 1 FROM TBL_USER_TEMPLATE
INSERT INTO TBL_USER_TEMPLATE ( TEMPLATE_NO, TEMPLATE_NM, USER_NO, USER_ID, REG_DT, REG_ID ) VALUES ( #{templateNo}, #{templateNm}, #{userNo}, #{userId}, NOW(), #{userId} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 정보 저장 * */
SELECT IFNULL(MAX(TEMPLATE_STEP_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP
INSERT INTO TBL_USER_TEMPLATE_STEP ( TEMPLATE_STEP_NO, TEMPLATE_NO, STEP, REG_DT, REG_ID, ALARM_DT, REPEAT_TYPE, ALARM_REPEAT_DAY, ALARM_TIME, STEP_X, STEP_Y, DEL_YN ) VALUES ( #{templateStepNo}, #{templateNo}, #{step}, NOW(), #{userId}, #{alarmDt}, #{repeatType}, #{alarmRepeatDay}, #{alarmTime}, #{stepX}, #{stepY}, #{delYn} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP_OBJ * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 오브젝트 저장 * */
SELECT IFNULL(MAX(TEMP_USER_OBJ_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP_OBJ
INSERT INTO TBL_USER_TEMPLATE_STEP_OBJ ( TEMP_USER_OBJ_NO, TEMPLATE_STEP_NO, CATE_NO, OBJ_NO, OBJ_NM, REG_DT, REG_ID, DEL_YN, LOC_NUM, STEP, TEMPLATE_NO ) VALUES ( #{tempUserObjNo}, #{templateStepNo}, #{cateNo}, #{objNo}, #{objNm}, NOW(), #{userId}, #{delYn}, #{locNum}, #{step}, #{templateNo} )

/** * com.swygbro.packup.template.mapper.TemplateMapper > TBL_USER_TEMPLATE_STEP_TEXT * 작성자 : 홍섭 * 설명 : 사용자 템플릿 스텝 텍스트 저장 * */
SELECT IFNULL(MAX(TEXT_NO), 0) + 1 FROM TBL_USER_TEMPLATE_STEP_TEXT
INSERT INTO TBL_USER_TEMPLATE_STEP_TEXT ( TEXT_NO, TEMPLATE_STEP_NO, TEXT, STEP_TEXT_X, STEP_TEXT_Y, TEMPLATE_NO, REG_DT, REG_ID, STEP ) VALUES ( #{textNo}, #{templateStepNo}, #{text}, #{stepTextX}, #{stepTextY}, #{templateNo}, NOW(), #{userId}, #{step} )

mapper.update:
UPDATE TBL_USER_TEMPLATE_STEP_OBJ
	SET 
		CATE_NO = #{cateNo},
		OBJ_NO = #{objNo},
		OBJ_NM = #{objNm},
		LOC_NUM = #{locNum},
		DEL_YN = #{delYn},
		UPD_ID = #{userId},
		UPD_DT = NOW(),
		STEP = #{step}
WHERE TEMP_USER_OBJ_NO = #{tempUserObjNo}
AND TEMPLATE_STEP_NO = #{templateStepNo}
AND STEP = #{step}
